import { DATA } from '../data/FGList'

import * as React from 'react';
import { useState } from 'react';
import Swipeable from 'react-native-gesture-handler/Swipeable';

import Constants from 'expo-constants';

import { StyleSheet, Text, View, SafeAreaView, SectionList, StatusBar, Button, Alert, FlatList } from "react-native";

const Item = ({ title }) => (
  <View style={styles.item}>
    <Text style={styles.title}>{title}</Text>
  </View>
);

export default function App() {
  const [listData, setListData] = useState(DATA);
  let row: Array<any> = [];
  let prevOpenedRow;

  /**
   *
   */
  const renderItem = ({ item, index }, onClick) => {
    //
    const closeRow = (index) => {
      console.log('closerow');
      if (prevOpenedRow && prevOpenedRow !== row[index]) {
        prevOpenedRow.close();
      }
      prevOpenedRow = row[index];
    };

    const renderRightActions = (progress, dragX, onClick) => {
      return (
        <View
          style={{
            margin: 0,
            alignContent: 'center',
            justifyContent: 'center',
            width: 70,
          }}>
          <Button color="red" onPress={onClick} title="DELETE"></Button>
        </View>
      );
    };

    return (
      <View style={styles.container}>
        <FlatList
          data={listData}
          renderItem={(v) =>
            renderItem(v, () => {
              console.log('Pressed', v);
              deleteItem(v);
            })
          }
          keyExtractor={(item) => item.id}></FlatList>
      </View>
    );
  };

  const deleteItem = ({ item, index }) => {
    console.log(item, index);
    let a = listData;
    a.splice(index, 1);
    console.log(a);
    setListData([...a]);
  };

  return (
    <><View style={styles.container}>
      <SectionList
        sections={DATA}
        keyExtractor={(item, index) => item + index}
        renderItem={({ item }) => <Item title={item} />}
        renderSectionHeader={({ section: { title } }) => (
          <Text style={styles.header}>{title}</Text>
        )} />
    </View><Button
        title="Add food"
        color="#f194ff"
        onPress={() => Alert.alert('Recipe scanned! Food added.')} />
    </>
    )

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    paddingTop: StatusBar.currentHeight,
    backgroundColor: '#ecf0f1',
    padding: 8,
    marginHorizontal: 16
  },
  item: {
    backgroundColor: "#b5c4b1",
    padding: 20,
    marginVertical: 3
  },
  header: {
    fontSize: 25,
    backgroundColor: "#bfbfbf"
  },
  title: {
    fontSize: 12
  },
  paragraph: {
    margin: 24,
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'center',
  }
})}
